sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here
	// Construct a GlideRecord query to find all NeedIt Task records with Due date
	// field values in the past for the same Assigned to.  The records cannot be in the
	// Closed state
	var rightnow= new GlideDateTime();
	var overdueNITask= new GlideRecord('x_187543_needitapp_needit_task');
	overdueNITask.query('duedate','<=',rightnow);
	overdueNITask.query('state','<',3);
	overdueNITask.query('assigned_to',current.assigned_to);

	overdueNITask.query();
	// Print to the Message field on a notification 
	//CHALLENGE Email Script so it does not print a message if the Assigned to has only 1 overdue NeedIt Task record.
	//var count= overdueNITask.getRowCount();
	//if (count>1){

		//template.print("In addition to this overdue NeedIt Task, you have " +(count-1)+ "more overdue task.");
	//}
	template.print("In addition to this overdue NeedIt Task, you have " +(overdueNITask.getRowCount()-1)+ "more overdue task.");

})(current, template, email, email_action, event);
