sys_script_include.script:
function validateEmailAddress(emailStr){
		// user javascript coercion to guarantee emailStr is a string
	emailStr= emailStr +'';
	//compare emailstr aginst the allowed syntex as specified in the regular expression
	//if emailStr has allowed syntex,return true else return false
	if(emailStr.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)) {
		return true;
	}

		else {
			return false;
		}
}
