sysauto_script.script:
var rightnow= new GildeDateTime();
// Query the database for NeedIt Task records with Due date field values within
// 24 hours from now.  Only return NeedIt Task records that do not have
// a State field value of Closed Complete.
var dueSoon= new GlideRecord('x_187543_needitapp_needit_task');
dueSoon.query('due_date','>=',rightnow);
dueSoon.query('due_date','<=',gs.hoursAgo(-24));
dueSoon.query('state','!=',3);

dueSoon.query();
//write a log message for each over due needit task
while(dueSoon.next()){
	gs.info("Needit Task due soon records=" + dueSoon.number);
	gs.eventQueue('x_187543_needitapp.dueSoonNeeditTasks',dueSoon,dueSoon.number,gs.getUserName());
}
